import styled from "styled-components";

const NotificationZone = styled.div`
  position: relative;
  display: inline-block;
  border-bottom: 1px dotted black;

  .tooltiptext {
    visibility: hidden;
    width: 100%;
    max-width: 400px;
    background-color: black;
    color: #fff;
    text-align: center;
    border-radius: 5px;
    padding: 5px 0;
    position: absolute;
    z-index: 1;
    top: 150%;
    left: 50%;
    margin-left: -110px;
  }

  .tooltiptext::after {
    content: "";
    position: absolute;
    bottom: 100%;
    left: 90%;
    margin-left: -5px;
    border-width: 5px;
    border-style: solid;
    border-color: transparent transparent black transparent;
  }
  :hover .tooltiptext {
    visibility: visible;
  }
`;

function App(props) {
  return (
    <>
      <NotificationZone {...props}>
        {props.children}
        <span class="tooltiptext">Tooltip text</span>
      </NotificationZone>
    </>
  );
}

export default App;
