import styled from "styled-components";

export const CourseCard = styled.div`
  filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
  width: 100%;

  .timetable {
    background: #0086c3;
    border-radius: 20px 0px 0px 20px;
    width: 227px;
    height: 186px;

    text-align: center;
    display: flex;
    justify-content: space-around;
    .date {
      font-family: Roboto;
      font-style: normal;
      font-weight: 500;
      font-size: 22px;
      line-height: 24px;

      letter-spacing: 0.15px;

      color: #ffffff;
    }
    .time {
      font-family: Roboto;
      font-style: normal;
      font-weight: 500;
      font-size: 18px;
      line-height: 24px;

      letter-spacing: 0.15px;

      color: rgba(255, 255, 255, 0.7);
    }
  }
`;
