import styled from "styled-components";

import Persona from "../assets/images/persona.png";

const VerticalBar = styled.div`
  position: fixed;
  width: ${(props) => (props.active ? "250px" : "50px")};
  z-index: 1001;
  height: 100%;
  left: 0px;
  top: 0px;
  padding: 0px 0px;
  background: #fff;
  transition: 0.125s;
  overflow-x: hidden;
  border-width: 0px 1px 0px 0px;
  border-image: linear-gradient(
      to bottom,
      #25256b 0px,
      #e5e5e5 0px,
      #e5e5e5 100%
    )
    5;
  border-style: solid;
  .label {
    font-family: Roboto;
    font-style: normal;
    font-weight: 700;
    font-size: 17px;
    color: #3d3d3d;
    vertical-align: middle;
    display: inline;
    text-decoration: none;
  }
  .icon {
    width: 30px;
    height: 30px;
    padding-right: 13px;
    vertical-align: middle;
    text-decoration: none;
  }

  .item {
    display: block;
    padding: 7px 10px;
    white-space: nowrap;
    text-decoration: none;
    cursor: pointer;
    :hover {
      background: #25256b;

      .label {
        color: #f4f4f4;
      }
      .icon {
        background: #25256b;
      }
      .path {
        fill: #f4f4f4;
      }
    }
  }
  .perfil {
    display: inline-block;
    width: 100%;

    img {
      width: ${(props) => (props.active ? "70px" : "40px")};
      height: ${(props) => (props.active ? "70px" : "40px")};
      margin: ${(props) => (props.active ? "10px" : "5px")};
      border-radius: 50px;
      transition: 0.125s;
      float: left;
    }
  }
`;

export default function SideBar(props) {
  return (
    <div>
      <VerticalBar {...props}>
        <div className="perfil">
          <img src={Persona} alt="" />
          asd
        </div>
      </VerticalBar>
    </div>
  );
}
