import styled from "styled-components";

const InputEfect = styled.div`
  position: relative;

  input {
    border: 1px solid #ccc;
    padding: 7px 14px;
    transition: 0.4s;
    background: transparent;
  }

  input ~ .focus-border {
    ::before,
    ::after {
      content: "";
      position: absolute;
      top: -1px;
      left: 50%;
      width: 0;
      height: 2px;
      background-color: #3399ff;
      transition: 0.4s;
    }
    :after {
      top: auto;
      bottom: 0;
    }
  }

  input:focus {
    ~ .focus-border {
      ::before,
      ::after {
        left: 0;
        width: 100%;
        transition: 0.4s;
      }
    }
    ~ label {
      top: -18px;
      left: 0;
      font-size: 12px;
      color: #3399ff;
      transition: 0.3s;
    }
  }

  input ~ .focus-border,
  input ~ .focus-border i:before,
  input ~ .focus-border i:after {
    content: "";
    position: absolute;
    top: 50%;
    left: 0;
    width: 2px;
    height: 0;
    background-color: #3399ff;
    transition: 0.6s;
  }
  input ~ .focus-border i:after {
    left: auto;
    right: 0;
  }
  input:focus ~ .focus-border i:before,
  input:focus ~ .focus-border i:after {
    top: -1px;
    height: 100%;
    transition: 0.6s;
  }
  label {
    position: absolute;
    left: 14px;
    width: 100%;
    top: 10px;
    color: #aaa;
    transition: 0.3s;
    z-index: -1;
    letter-spacing: 0.5px;
  }
`;

export default function App() {
  return (
    <InputEfect>
      <input class="effect-19" name="teste" type="text" placeholder="" />
      <label from="teste">First Name</label>
      <span class="focus-border">
        <i></i>
      </span>
    </InputEfect>
  );
}
