import styled from "styled-components";
import { useState } from "react";

const NotificationZone = styled.div`
  position: relative;
  display: inline-block;
  border-bottom: 1px dotted black;

  .tooltiptext {
    visibility: ${(props) => (props.active ? "visible" : "hidden")};
    max-width: 500px;
    width: 100vw;
    background-color: black;
    color: #fff;
    text-align: center;
    border-radius: 5px;
    padding: 5px 0;
    position: absolute;
    z-index: 1;
    top: 100%;
    left: 50%;
    margin-left: -490px;
  }

  .tooltiptext::after {
    content: "";
    position: absolute;
    bottom: 100%;
    right: 5px;
    margin-left: -5px;
    border-width: 5px;
    border-style: solid;
    border-color: transparent transparent black transparent;
  }
`;

function App(props) {
  const [notifiationActive, setNotifiationActive] = useState(true);
  const changeNotifiationActive = () =>
    setNotifiationActive(!notifiationActive);

  return (
    <>
      <NotificationZone {...props} active={notifiationActive}>
        {props.children}
        <span class="tooltiptext">Tooltip text</span>
      </NotificationZone>
    </>
  );
}

export default App;
