import styled from "styled-components";

const InputEfect = styled.div`
  position: relative;
  text-align: left;
  margin: 20px 10px;

  input {
    border: 0;
    padding: 4px 0;
    border-bottom: 1px solid #ccc;
    background-color: transparent;
    font: 15px/24px "Lato", Arial, sans-serif;
    color: #333;
    width: 100%;
    box-sizing: border-box;
    letter-spacing: 1px;
  }
  input:focus {
    ~ .focus-border {
      position: absolute;
      bottom: 0;
      left: 0;
      width: 0;
      height: 2px;
      background-color: #3399ff;
      transition: 0.4s;
    }
    ~ label {
      top: -16px;
      font-size: 12px;
      color: #1d4657;
      transition: 0.3s;
    }
  }
  input ~ .focus-border {
    position: absolute;
    bottom: 0;
    left: 50%;
    width: 0;
    height: 2px;
    background-color: #1d4657;
    transition: 0.4s;
  }
  label {
    position: absolute;
    left: 0;
    width: 100%;
    top: 9px;
    color: #aaa;
    transition: 0.3s;
    z-index: -1;
    letter-spacing: 0.5px;
  }
`;

export default function App() {
  return (
    <InputEfect>
      <input type="text" placeholder="" />
      <label from="teste">First Name</label>
      <span class="focus-border"></span>
    </InputEfect>
  );
}
