import styled from "styled-components";

export const CourseCard = styled.div`
  filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
  width: 100%;
  display: flex;
  flex-direction: row;

  .timetable {
    background: #0086c3;
    border-radius: 20px 0px 0px 20px;
    width: 227px;
    height: 186px;

    text-align: center;
    display: flex;
    justify-content: space-around;
    flex-direction: column;

    .date {
      font-family: Roboto;
      font-style: normal;
      font-weight: 500;
      font-size: 22px;
      line-height: 24px;

      letter-spacing: 0.15px;

      color: #ffffff;
    }
    .time {
      font-family: Roboto;
      font-style: normal;
      font-weight: 500;
      font-size: 18px;
      line-height: 24px;

      letter-spacing: 0.15px;

      color: rgba(255, 255, 255, 0.7);
    }
  }
  .informations {
    background-color: #fff;
    display: flex;
    justify-content: space-around;
    flex-direction: column;
    height: 186px;

    width: ${() => "calc(100% - 227px)"};
    border: 1px solid #e5e5e5;

    .title {
      font-family: Roboto;
      font-style: normal;
      font-weight: 500;
      font-size: 18px;
      line-height: 24px;

      /* identical to box height, or 133% */
      letter-spacing: 0.15px;
    }
  }
`;
