import styled, { createGlobalStyle } from "styled-components";
import { useState } from "react";

import SideBar from "../styles/sidebar";
import HeaderBar from "../styles/headerbar";

const AppFrame = styled.div`
  margin-left: ${(props) => (props.active ? "350px" : "50px")};
`;

const GlobalStyle = createGlobalStyle`
    *{
      margin: 0px;
      padding: 0px; 
    }
`;

export default function App(props) {
  const [barActive, setBarActive] = useState(false);

  const changeBarActive = () => {
    setBarActive(!barActive);
    console.log(barActive);
  };

  return (
    <>
      <GlobalStyle />
      <SideBar active={barActive} />
      <AppFrame active={barActive}>
        <HeaderBar changeBarActive={changeBarActive} />
        {props.children}
      </AppFrame>
    </>
  );
}
